using System.Net;
using System.Net.Mail;

namespace urele.Service.Helper
{
    public class SendMail
    {
        public static async Task<bool> sendMail(string to, string caption, string message)
        {
            try
            {
                bool isBroken = false;
                var client = new SmtpClient("smtp-mail.outlook.com", 587)
                {
                    Credentials = new NetworkCredential("urele@outlook.com.tr", "uLeLEpArolAaA745*BSM$"),
                    EnableSsl = true
                };
                await Task.Run(() =>
                {
                    try
                    {
                        client.Send(new MailMessage("urele@outlook.com.tr", to)
                        {
                            Subject = caption,
                            Body = message,
                            IsBodyHtml = true,
                        });
                    }
                    catch
                    {
                        isBroken = true;
                    }
                });
                if (isBroken)
                {
                    throw new Exception("Email cannot send");
                }
                return true;
            }
            catch
            {
                return false;
            }
        }
